
public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a, b, c;
		
		a = 2;
		b = 5;
		c = 8;
		
		a -= 4;
		b *= a;
		c++;
		
		System.out.println("Value of a: " + a);
		System.out.println("Value of b: " + b);
		System.out.println("Value of c: " + c);
	}

}
